import { useState, useEffect } from 'react';
import { Container } from '@material-ui/core';
import GoogleMap from '../Components/GoogleMap';

const Details = (props) => {
  const id = props.match.params.id;
  const selectedListing = props.listings.find(listing => listing.id === id);

  const [latt, setLatt] = useState(35.227200);
  const [long, setLong] = useState(-80.846100);

  useEffect(() => {
    fetch(`https://maps.googleapis.com/maps/api/geocode/json?address=${filteredListing.address}&key=${process.env.REACT_APP_MAP_API_KEY}`)
      .then(response => response.json())
      .then(data => {
         console.log(data);
        setLatt(data.results[0].geometry.location.lat);
        setLong(data.results[0].geometry.location.lng);
      })
      .catch(error => {
        console.log(error);
      })

  });

  return (
    <div>
      <Container maxWidth="xs">
        <h2>{selectedListing.name}</h2>
        <h4>{selectedListing.address}</h4>
        <h4>{selectedListing.hours}</h4>
        <p>{selectedListing.description}</p>
        <GoogleMap latt={latt} long={long} listingName={selectedListing.name} />
      </Container>
    </div>
  )
}

export default Details;
